window.addEventListener("load",init);function init(a){desktop.open();gusers.open()}function start_up_env(a){login.close();new Gwt.Core.Request("/backend/sys/",{action:"start_up_env",username:a},function(b){sessionStorage.setItem("session","active");sessionStorage.setItem("group",b.response.group);sessionStorage.setItem("user",b.response.user);start_session()})}function start_session(a){lancelot.open();document.onmousemove=renueve_session;document.onkeypress=renueve_session;if(typeof(session_env)!="undefined"){clearTimeout(session_env)}session_env=setTimeout(block_session,60000)}function block_session(){sessionStorage.setItem("session","block");lancelot.close();block.open();if(typeof(session_env)!="undefined"){clearTimeout(session_env)}session_env=setTimeout(close_session,60000)}function unlock_session(){clearTimeout(session_env);session_env=null;block.close();login.open()}function renueve_session(){if(sessionStorage.hasOwnProperty("session")){if(sessionStorage.getItem("session")!="block"){clearTimeout(session_env);session_env=setTimeout(block_session,60000)}}}function close_session(){clearTimeout(session_env);session_env=null;new Gwt.Core.Request("/backend/auth/",{action:"logout"},function(a){console.log(a)});sessionStorage.clear();block.close();login.open()};